import React from "react";
import styled from "styled-components";

import LogoImg from '../../images/logos/logo.png';
// container for logo
const BrandLogoContainer = styled.div`
    display:flex;
    align-items:center;
`;

const LogoImage = styled.div`
    width: ${( { size } ) => size ? size + "px" : "3em"};
    height: ${({ size }) => size ? size + "px" : "3em"};
    margin: 1em 0.2em 1em 1em;
    img{
        width:100%;
        height:100%; 
    }
`;

const LogoTitle = styled.h2`
    margin:1em 0 0 1em;
    font-size: ${({ size }) => size ? size + "px" : "15px"};
    color: ${({ color }) => color ? color : "#000"};
    font-weight: 900;

`;

export function BrandLogo(props){
    const { logoSize, textSize, color, hideLogo } = props;

    return (
        <BrandLogoContainer >
        {!hideLogo && <LogoImage size={logoSize}>
          <img src={LogoImg} alt="logo"/>
        </LogoImage>}
        <LogoTitle size={textSize} color={color}>Pawfect Solutions</LogoTitle>
        </BrandLogoContainer>
    );
}
